const VDStringHelper=require("./string.js"),fs=require("fs");class VDFileHelper{static getExtension(e){if(VDStringHelper.isEmpty(e))return null;let t=e.lastIndexOf(".");return-1===t?null:e.substring(t+1,e.length).toLowerCase()}humanFileSize(e,t=!1,r=2){const i=t?1e3:1024;if(Math.abs(e)<i)return e+" B";const n=t?["kB","MB","GB","TB","PB","EB","ZB","YB"]:["KiB","MiB","GiB","TiB","PiB","EiB","ZiB","YiB"];let s=-1;const l=10**r;do{e/=i,++s}while(Math.round(Math.abs(e)*l)/l>=i&&s<n.length-1);return e.toFixed(r)+" "+n[s]}static appendStringToFile(e,t){try{return fs.appendFileSync(e,t),!0}catch(e){return!1}}static createFolder(e){try{return fs.existsSync(e)||fs.mkdirSync(e,{recursive:!0}),!0}catch(e){return!1}}static getFileSize(e){try{return fs.statSync(e).size}catch(e){return 0}}}module.exports=VDFileHelper;