const libphonenumber=require("google-libphonenumber");class VDPhoneHelper{static phoneUtil=libphonenumber.PhoneNumberUtil.getInstance();static phoneFormat=libphonenumber.PhoneNumberFormat;static removeCountryCode(e,r="ZZ"){try{const o=e.startsWith("+")?e:"+"+e,t=this.phoneUtil.parse(o,r);return this.phoneUtil.format(t,this.phoneFormat.NATIONAL).replace(/\D/g,"")}catch(r){return console.error("Error parsing phone number:",r),e}}}module.exports=VDPhoneHelper;